mapper.update:
UPDATE setmeal
SET status = #{status}
WHERE id = #{id}
  AND status != #{status}

mapper.delete:
DELETE FROM setmeal
WHERE id IN

    #{item}

mapper.select:
SELECT COUNT(id)
FROM setmeal
WHERE category_id = #{categoryId}

SELECT 
FROM setmeal


        AND status = #{status}


        AND category_id = #{categoryId}


        AND name LIKE CONCAT('%', #{name}, '%')


ORDER BY id DESC
